version.script:
import org.efaps.admin.runlevel.RunLevel;
import org.efaps.db.Context;
import org.efaps.db.SearchQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;

import java.net.URL
import java.io.File;

EFAPS_LOGGER.info("Importing Data for eFapsApp-Archives");

RunLevel.init("shell");
RunLevel.execute();

SearchQuery query = new SearchQuery();
query.setQueryTypes("Admin_User_JAASKey");
query.addWhereExprEqValue("Key", "Archives_Admin");
query.addSelect("OID");
query.execute();
if (!query.next()) {
    URL url1 = new URL(EFAPS_ROOTURL, "Data/IMPORT_UserJAASKey.xml");
    DataImport imp1 = DataImport.readFile(url1);
    imp1.updateInDB();
}


UIUpdate update = new UIUpdate();
update.add2Menu("20638332-8177-4aa7-9c46-5cd78d0c567d", "87001cc3-c45c-44de-b8f1-776df507f268", 1);
